import { useEffect, useState } from 'react'
// import { API_BASE_URL } from '../../../../constans/api'
import Input from '../Input'
import { validateForm } from '../../../../utils/helpers'


const Form = ({className, form}) => {
  const {
    fields, 
    connection: {
      label,
      options
    }, 
    policy: {
      checkbox,
      linkPolicy: {
        url,
        data
      }
    }, 
    buttonText
  } = form

  const [order, setOrder] = useState({
    name: '',
    tel: '',
    email: '',
    connection: '',
    isPolicy: true
  })
  const [isValidate, setIsValidate] = useState({
    nameValid: false,
    telValid: false,
    emailValid: false,
    connectionValid: false
  })

  const handleOnChange = (event, validate) => {
    const {name, value, type, checked} = event.target
    
    console.log(event.target.value);

    name === 'connection'
    ? setIsValidate({
        ...isValidate,
        [`${name}Valid`]: true
      })
    : setIsValidate({
        ...isValidate,
        [`${name}Valid`]: validate
      })  

    type === 'checkbox' 
    ? setOrder({
      ...order,
      [name]: checked
    })
    : setOrder({
      ...order,
      [name]: value,
      data: new Date().toLocaleString(),
      type: 'order'
    })
  }

  const handleFormSubmit = (event) => {
    event.preventDefault()

    console.log({order})

    // fetch(`${API_BASE_URL}/orders/.json`, {
    //   method: 'POST',
    //   body: JSON.stringify(order)
    // })
    //   .catch(error => console.error(error))
  }

  
  return (
    <form 
      className={`${className}__form`} 
      onSubmit={handleFormSubmit}
    >      
      {fields.length && (
        fields.map(field => 
          <Input 
            className={className} 
            field={field}
            key={field.type}
            order={order}
            handleOnChange={handleOnChange}
          />
        )
      )}
      <label 
        className={order.connection
          ? `${className}__select success`
          : `${className}__select error`
        }
      >
        <span>{label}</span>          
        <select
          name='connection'
          value={order.connection}
          onChange={handleOnChange}
        >
          <option></option>
          {options.length && (
            options.map(option => 
              <option 
                value={option.value} 
                key={option.value}
              >
                {option.text}
              </option>
            )
          )}
        </select>
      </label>    
      <label className={`${className}__policy`}>
        <input 
          type={checkbox.type} 
          name='isPolicy'
          checked={order.isPolicy}
          onChange={handleOnChange}
        />
        <a href={url}>
          {data}
        </a>
      </label>
      <button 
        className={`${className}__btn`} 
        type='submit'
        disabled={isValidate ? 'enable' : 'disabled'} 
      >
        <span>
          {buttonText ? buttonText : 'Submit'}
        </span>
      </button>      
    </form>
  )
}

export default Form
