import MenuItem from './MenuItem'

const Menu = ({className, menus}) => {
  const currentClassName = className
    ? `${className}__menu`
    : 'menu'

  return(
    <ul data-name='menu' className={currentClassName}>
      {menus.length && (
          menus.map((menu, i) => 
            <li className={`${currentClassName}__item`} key={menu.target}>
              <button 
                data-btn="menu" 
                data-target={menu.target}
              >
                <span>{menu.data}</span>
              </button>
            </li>
          )
        )
      }
    </ul>
  )
}

export default Menu
