import MenuItem from './MenuItem'

const Menu = ({className,
  menuItems, 
  isBurgerActive, onSectionToScroll}) => {
  return (
    <ul className={`${className}__menu`}>
      
      {menuItems.length && 
        menuItems.map(item => 
          <MenuItem 
            className={className} 
            item={item} 
            key={item.target}
            onSectionToScroll={onSectionToScroll}
          />
      )} 
    </ul>
  )
}

export default Menu
