import { renderToString } from 'react-dom/server'
import { ReactComponent as SunIcon } from '../layout/Header/assets/sun_icon.svg'
import { ReactComponent as MoonIcon } from '../layout/Header/assets/moon_icon.svg'
import {
  removeBurgerAndMenuActive,
  validateForm
} from './helpers.js'

export const handleScrollToHeaderClick = () => {
  removeBurgerAndMenuActive()  
  window.scrollTo({
    top: 0,
    behavior: 'smooth'
  })
}

export const handleThemeButtonClick = (event, clients) => {
  const $body = document.body
  const $themeBtn = event.currentTarget
  const $header = $body.querySelector('[data-name="header"]')
  const $menu = $body.querySelector('[data-name="menu"]')
  const $burger = $body.querySelector('[data-btn="burger"]')
  const $orderBtn = $body.querySelector('[data-btn="modal"]')
  const $clientsNodes = document.querySelectorAll('[data-type="clients"')
  const darkThemeClients = clients.darkThemeClients
  const lightThemeClients = clients.lightThemeClients

  console.log(clients);
  


  !$body.classList.contains('dark') 
    ?  clientsNodes.forEach((clientsNode, index) => {
        clientsNode.src = darkThemeClients[index].source
      })
    : clientsNodes.forEach((clientsNode, index) => {
        clientsNode.src = lightThemeClients[index].source
      })
    
  const sun = renderToString(<SunIcon/>)
  const moon = renderToString(<MoonIcon/>)

  if ($themeBtn.dataset.theme === 'moon') {
    $themeBtn.dataset.theme = 'sun'
    $themeBtn.innerHTML = sun
  } else {
    $themeBtn.dataset.theme = 'moon'
    $themeBtn.innerHTML = moon
  }
  
}
