import Navigation from './components/Navigation'
import { ReactComponent as MoonIcon } from './assets/moon_icon.svg'
import { handleBurgerButtonClick, handleThemeButtonClick } from '../../utils/handlers'

const Header = ({className, data, clients}) => {
  const {name, navigation} = data

  return (
    <header 
      data-name={name}
      className={className ? className : 'header'} 
    >
      <div className={`${className}__wrapper wrapper`}>
        <nav className={`${className}__nav`}>

        </nav>

        <button 
          data-btn="theme" 
          data-theme="moon"
          className={`${className}__theme`}
          onClick={(e) => handleThemeButtonClick(e, clients)}
        >
          <MoonIcon/>
        </button>

        <button 
          data-btn="burger"
          className={`${className}__burger`}
          onClick={handleBurgerButtonClick} 
        >
          <span></span>
        </button>    
      </div>
    </header>
  )
}

export default Header
