import {ReactComponent as CloseIcon} from './assets/close_icon.svg'
import Title from '../../components/Title/Title'
import Form from './components/Form'
import { handleModalButtonCloseClick } from '../../utils/handlers'

const Modal = ({className, data}) => {
  const {
    title, 
    form
  } = data

  return (
    <div 
      data-name={className}
      className={`${className}`}
    >
      <div className={`${className}__body`}>
        <button 
          data-btn="close"
          className={`${className}__close`} 
          onClick={handleModalButtonCloseClick}
        >
          <CloseIcon/>
        </button>
        {title && (
          <Title 
            data-name='tittl'
            className={className} 
            title={title}
          />
        )}
        {form && (
          <Form
            className={className}
            form={form}
          />
        )}
      </div>      
    </div>  
  )
}

export default Modal
