import Input from './Input.js'
import Option from './Option.js'
import Policy from './Policy.js'

const Form = ({className, form}) => {
  const {
    fields, 
    connection, 
    policy, 
    buttonText
  } = form

  const currentClassName = className
    ? `${className}__form`
    : 'form'
  const childClassName = className
    ? className
    : currentClassName
    
  return (
    <form className={currentClassName} data-name='form' id='form'>      
      {fields.length && (
        fields.map(field => 
          <Input 
            className={childClassName} 
            field={field}
            key={field.type}
          />
        )
      )}
      <label 
        data-name="connection"
        class={`className`}
      >
        <span>{connection.label}</span>          
        <select>
          <option></option>
          {connection.options.length && (
            connection.options.map(option => 
            <Option option={option}/>
            )
          )}
        </select>
      </label>    
      {/* ${policy ? Policy(policy, childClassName) : ''} */}
      <button 
        data-name='submit'
        className={`${childClassName}__btn`} 
        type='submit' 
        disabled
      >
        <span>
          {buttonText ? buttonText : 'Submit'}
        </span>
      </button>      
    </form>
  )
}

export default Form
