  // const {menus} = navigation
  const currentClassName = className
    ? `${className}__nav`
    : 'nav'
  // const childClassName = parentClassName
  //   ? parentClassName
  //   : currentClassName

  return (
    <nav className={currentClassName}>     
      ${Logo('header', childClassName)}
      ${menus ? Menu(menus, childClassName) : ''}
      <button 
        data-btn="theme" 
        data-theme="moon"
        class="${childClassName}__theme" 
      >
        ${MoonIcon()}
      </button>
    </nav>
  )
}

export default Navigation
