import { useState, useEffect } from 'react'
import { API_BASE_URL } from './constans/api'
import Header from './layout/Header'
import Download from './layout/Download'
import Warranty from './layout/Warranty'
import Care from './layout/Care'
import Cashback from './layout/Cashback'
import Clients from './layout/Clients'
import Footer from './layout/Footer'
import Modal from './layout/Modal'

const App = () => {
  const [data, setData] = useState({})
  const [isLogoClicked, setIsLogoClicked] = useState(false)
  const [isDarkTheme, setIsDarkTheme] = useState(() => {
    return localStorage.getItem('isDarkTheme') === 'true'
  })
  const [isBurgerClicked, setIsBurgerClicked] = useState(false)
  const [isSectionScrolling, setIsSectionScrolling] = useState('')
  const [isModalActive, setIsModalActive] = useState(false)

  useEffect(() => {
    fetch(`${API_BASE_URL}.json`)
      .then(response => {
        if (response.ok) {
          return response.json()
        }
      })
      .then(data => setData(data))
      .catch(error => console.error(error))
  }, [])
  useEffect(() => {
    setIsBurgerClicked(false)    
    window.scroll({
      top: 0,
      behavior: 'smooth'
    }
    )    
  }, [isLogoClicked])
  useEffect(() => {
    localStorage.setItem('isDarkTheme', `${isDarkTheme}`)

    isDarkTheme 
      ? document.body.classList.add('dark')
      : document.body.classList.remove('dark')
  },[isDarkTheme])
  useEffect(() => {
    const $menu = document.querySelector('[data-name="menu"]')

    $menu && (isBurgerClicked
      ? $menu.classList.add('active')
      : $menu.classList.remove('active')
    )
  }, [isBurgerClicked])
  useEffect(() => {
    isSectionScrolling && 
      handleScrollToSection(isSectionScrolling)
  }, [isSectionScrolling])
  usee

  const handleLogoClick = () => {
    setIsLogoClicked(!isLogoClicked)
  }
  const handleClickTheme =() => {
    setIsDarkTheme(!isDarkTheme)    
  }
  const handleClickBurger = () => {
    setIsBurgerClicked(!isBurgerClicked)
  }
  const handleSectionScroll = (event) => { 
    setIsSectionScrolling(event.currentTarget.dataset.target)
    setIsBurgerClicked(false)
  }

  const handleScrollToSection = (target) => {
    const $section = document.querySelector(`[data-name="${target}"]`)
    const $header = document.querySelector('[data-name="header"]')

    const clientPosition = window.pageYOffset
    const sectionPosition = $section.getBoundingClientRect().top
    const headerHeight = $header.getBoundingClientRect().height
    const targetPosition = clientPosition + sectionPosition - headerHeight

    window.scrollTo({
      top: targetPosition,
      behavior: 'smooth'
    })
  }

  return (
    <>    
      {data.header && (
        <Header 
          className='header'
          data={data.header}
          isDarkTheme={isDarkTheme}
          onClickLogo={handleLogoClick}
          onClickTheme={handleClickTheme}
          onClickBurger={handleClickBurger}
          onSectionScroll={handleSectionScroll}
        />
      )}
      {data.download && (
        <Download
          className='download'
          data={data.download}
        />
      )}
      {data.warranty && (
        <Warranty
          className='warranty'
          data={data.warranty}
        />
      )}
      {data.care && (
        <Care
          className='care'
          data={data.care}
        />
      )}
      {data.cashback && (
        <Cashback 
          className='cashback'
          data={data.cashback}
        />
      )}
      {data.clients && (
        <Clients
          className='clients'
          data={data.clients}
          isDarkTheme= {isDarkTheme}
        />
      )}
      {data.footer && (
        <Footer
          className='footer'
          data={data.footer}
        />
      )}
      {data.modal && (
        <Modal
          className='modal'
          data={data.modal}
        />
      )}
    </>
  )
}

export default App
