  return `
    <nav class="${currentClassName}">     
      ${Logo('header', childClassName)}
      ${menus ? Menu(menus, childClassName) : ''}
      <button 
        data-btn="theme" 
        data-theme="moon"
        class="${childClassName}__theme" 
      >
        ${MoonIcon()}
      </button>
    </nav>
  `
}

export default Navigation
