import Title from '../../../components/Title/Title'
// import ColumnItem from './ColumnItem'

const Column = (className, column) => {
  const {title, links} = column

  const currentClassName = className
    ? `${className}__column`
    : 'column'
  const childClassName = className
    ? className
    : currentClassName

  return (
    <div class={currentClassName}>
      ${title ? Title(title, childClassName) : ''}
      <ul class="${childClassName}__list">
        ${links?.length 
          ? links.map((link) => ColumnItem(link, childClassName)).join('') 
          : ''
        }
      </ul>
    </div>
  )
}

export default Column
