import { handleInputKeyup, handleModalFormClick, handleModalFormSubmit, handleModalSelectChange } from "../../../utils/handlers"

const Form = ({className, form}) => {
  const {
    fields, 
    connection: {
      label,
      options
    }, 
    policy: {
      checkbox,
      linkPolicy: {
        url,
        data
      }
    }, 
    buttonText
  } = form

  const currentClassName = className
    ? `${className}__form`
    : 'form'
  const childClassName = className
    ? className
    : currentClassName

  return (
    <form 
      data-name='form' 
      className={currentClassName} id='form'
      onClick={handleModalFormClick}
    >      
      {fields.length && (
        fields.map(field => 
          <input
            data-input={field.type}
            data-success="false"
            className={`${childClassName}__input`} 
            type={field.type} 
            placeholder={field.placeholder}
            key={field.placeholder}
            onKeyUp={handleInputKeyup}
          />
        )
      )}
      <label 
        data-name="connection"
        className={`${className}__select`}
      >
        <span>{label}</span>          
        <select
          onChange={handleModalSelectChange}
        >
          <option></option>
          {options.length && (
            options.map(option => 
              <option value={option.value} key={option.value}>{option.text}</option>
            )
          )}
        </select>
      </label>    
      <label className={`${className}__policy`}>
        <input type={checkbox.type} data-name="policy"/>
        <a href={url}>
          {data}
        </a>
      </label>
      <button 
        data-name='submit'
        className={`${childClassName}__btn`} 
        type='submit' 
        disabled
        onSubmit={handleModalFormSubmit}
      >
        <span>
          {buttonText ? buttonText : 'Submit'}
        </span>
      </button>      
    </form>
  )
}

export default Form
