import { useState, useEffect } from 'react'
import { API_BASE_URL } from './constans/api'
import Header from './layout/Header'
import Download from './layout/Download'
import Warranty from './layout/Warranty'
import Care from './layout/Care'
import Cashback from './layout/Cashback'
import Clients from './layout/Clients'
import Footer from './layout/Footer'
import Modal from './layout/Modal'
import { scrollToSection, burgerActive } from './utils/handlers'

const App = () => {
  const [isLoading, setIsLoading] = useState(true)
  const [data, setData] = useState({})
  const [isLogoClicked, setIsLogoClicked] = useState(false)
  const [isDarkTheme, setIsDarkTheme] = useState(() => {
    return localStorage.getItem('isDarkTheme') === 'true'
  })
  const [isBurgerActive, setIsBurgerActive] = useState(false)
  const [isSectionScrolling, setIsSectionScrolling] = useState('')
  const [isModalActive, setIsModalActive] = useState(true)

  useEffect(() => {
    fetch(`${API_BASE_URL}.json`)
      .then(response => {
        if (response.ok) {
          return response.json()
        }
      })
      .then(data => setData(data))
      .catch(error => console.error(error))
  }, [])

  useEffect(() => {
    if (Object.values(data).length) {
      const timerId = setTimeout(() => {
        setIsLoading(false)     
      }, 1000);

      return () => {
        clearTimeout(timerId)
      }
    }
  }, [data])

  useEffect(() => {
    setIsBurgerActive(false)    
    window.scroll({
      top: 0,
      behavior: 'smooth'
    }
    )    
  }, [isLogoClicked])

  useEffect(() => {
    localStorage.setItem('isDarkTheme', isDarkTheme)

    if (isDarkTheme) {
      document.body.classList.add('dark')
    } else {
      document.body.classList.remove('dark')
    }
  }, [isDarkTheme])


  useEffect(() => {
    burgerActive(isBurgerActive)
  }, [isBurgerActive])

  useEffect(() => {
    isSectionScrolling && 
      scrollToSection(isSectionScrolling)
  }, [isSectionScrolling])

  useEffect(() => {
    if (isModalActive) {
      document.body.classList.add('hidden')
    } else {
      document.body.classList.remove('hidden')
    }
  }, [isModalActive])  

  const handleLogoScrollClick = () => {
    setIsLogoClicked(!isLogoClicked)
  }

  const handleThemeChangeClick = () => {
    setIsDarkTheme(!isDarkTheme)    
  }

  const handleBurgerActiveClick = () => {
    setIsBurgerActive(!isBurgerActive)
  }

  const handleSectionToScrollClick = (event) => { 
    setIsSectionScrolling(event.currentTarget.dataset.target)
    setIsBurgerActive(false)
  }

  const handleModalButtonActiveClick = () => {
    setIsModalActive(true)
  }

  const handleModalCloseClick = () => {
    setIsModalActive(false)
  }


  return (
    <>
      {isLoading && (
        <div className='preloader'>
          Loading...
        </div>
      )}

      {data.header && (
        <Header 
          className='header'
          data={data.header}
          isDarkTheme={isDarkTheme}
          onLogoClick={handleLogoScrollClick}
          onThemeClick={handleThemeChangeClick}
          onBurgerClick={handleBurgerActiveClick}
          onSectionToScrollClick={handleSectionToScrollClick}
        />
      )}

      {data.download && (
        <Download
          className='download'
          data={data.download}
        />
      )}

      {data.warranty && (
        <Warranty
          className='warranty'
          data={data.warranty}
        />
      )}

      {data.care && (
        <Care
          className='care'
          data={data.care}
        />
      )}

      {data.cashback && (
        <Cashback 
          className='cashback'
          data={data.cashback}          
          onModalButtonActiveClick={handleModalButtonActiveClick}
        />
      )}

      {data.clients && (
        <Clients
          className='clients'
          data={data.clients}
          isDarkTheme= {isDarkTheme}
        />
      )}

      {data.footer && (
        <Footer
          className='footer'
          data={data.footer}
        />
      )}

      {data.modal && (
        <Modal
          className='modal'
          data={data.modal}
          isModalActive={isModalActive}
          onModalCloseClick={handleModalCloseClick}
        />
      )}
    </>
  )
}

export default App
