import MenuItem from './MenuItem'

const Menu = ({className, menus}) => {
  const currentClassName = className
    ? `${className}__menu`
    : 'menu'
  const childClassName = className
    ? className
    : currentClassName

  return(
    <ul data-name='menu' className={currentClassName}>
      {menus && (
          menus.map((menu, i) => 
            <MenuItem 
              className={childClassName} 
              menu={menu} key={i}
            />
          )
        )
      }
    </ul>
  )
}

export default Menu
