import Title from '../../components/Title/Title'
import Text from '../../components/Text/Text'
import Image from '../../components/Image/Image'
import Link from './components/Link'

const Download = ({className, data}) => {
  const {title, texts, links, image} = data

  return (
    <section 
      data-name={className}
      className={className ? className : 'download'}
    >
      <div className='container'>
        <div className={`${className}__wrapper`}>
          <div className={`${className}__body`}>
            {title && 
              <Title className={className} title={title}/> 
            }
            {texts && (
              texts.map((text, i) => 
                <Text className={className} text={text} key={i}/>
              )
            )}
            <div className={`${className}__links`}>
              {links && (
                links.map((link, i) => 
                  <Link className={className} link={link} key={i}/>
                )
              )}    
            </div>               
          </div>
          <div className={`${className}__image`}>
            <Image dataType={className} image={image}/>
          </div>
        </div>
      </div>
    </section>
  )
}

export default Download
